import React, { useState } from 'react';
import BotCollection from './components/';

const App = () => {
  // Define the initial army state as an empty array
  const [army, setArmy] = useState([]);

  // Define the addToArmy function to add a bot to the army
  const addToArmy = (bot) => {
    // Add the bot to the army state
    setArmy((prevArmy) => [...prevArmy, bot]);
  };

  return (
    <div>
      {/* Pass the addToArmy function as a prop to the BotCollection component */}
      <BotCollection addToArmy={addToArmy} />
      {/* Render the YourBotArmy component here */}
    </div>
  );
};

export default App;
