import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import BotCollection from './components/BotCollection';
import YourBotArmy from './components/YourBotArmy';
import axios from 'axios';

const App = () => {
  const [army, setArmy] = useState([]);

  const addToArmy = (bot) => {
    if (!army.includes(bot)) {
      setArmy([...army, bot]);
    }
  };

  const removeFromArmy = (bot) => {
    const updatedArmy = army.filter((b) => b.id !== bot.id);
    setArmy(updatedArmy);
  };

  const removeFromDB = (botId) => {
    axios.delete(`http://localhost:8001/bots/${botId}`)
      .then(() => {
        const updatedArmy = army.filter((b) => b.id !== botId);
        setArmy(updatedArmy);
      })
      .catch((error) => console.error(error));
  };

  return (
    <Router>
      <div>
        <h1>Bot Battlr</h1>
        <nav>
          <ul>
            <li>
              <Link to="/">Bot Collection</Link>
            </li>
            <li>
              <Link to="/army">Your Bot Army</Link>
            </li>
          </ul>
        </nav>

        <Route exact path="/">
          <BotCollection addToArmy={addToArmy} />
        </Route>
        <Route path="/army">
          <YourBotArmy army={army} removeFromArmy={removeFromArmy} />
        </Route>
      </div>
    </Router>
  );
};

export default App;
