import React from 'react';

const YourBotArmy = ({ army, removeFromArmy, removeFromDB }) => {
  return (
    <div>
      <h2>Your Bot Army</h2>
      {army.map((bot) => (
        <div key={bot.id}>
          <h3>{bot.name}</h3>
          <img src={bot.avatar_url} alt={bot.name} />
          <button onClick={() => removeFromArmy(bot)}>Release Bot</button>
          <button onClick={() => removeFromDB(bot.id)}>Discharge Bot</button>
        </div>
      ))}
    </div>
  );
};

export default YourBotArmy;
