import React, { useState } from 'react';
import BotCollection from './components';

const App = () => {
  // Define the addToArmy function in the main component
  const addToArmy = (bot) => {
    // Logic to add the bot to the army goes here
    console.log(`Adding ${bot.name} to the army`);
  };

  return (
    <div>
      {/* Pass the addToArmy function as a prop to the BotCollection component */}
      <BotCollection addToArmy={addToArmy} />
    </div>
  );
};

export default App;
